create table reisi (
	reisi_id INT,
	lähte_linn VARCHAR(50),
	saabumise_linn VARCHAR(50),
	valjumis_aeg VARCHAR(50),
	saabumise_aeg VARCHAR(50),
	lennujaama_nimi VARCHAR(50),
	hind INT,
	transport_hind INT
);

SELECT
    reisi_id AS '@reisi_id',
	hind AS 'Lend/Hind',
    lähte_linn AS 'Lend/Valjumine/Linn',
    valjumis_aeg AS 'Lend/Valjumine/Aeg',
    saabumise_linn AS 'Lend/Saabumine/Linn',
    saabumise_aeg AS 'Lend/Saabumine/Aeg',
    lennujaama_nimi AS 'Lend/LennujaamaNimi',
	transport_hind AS 'Lend/TransportHind'
	
FROM reisi
FOR XML PATH('Reisi'), ROOT('Reisid')
